# -*- coding: utf-8 -*-
import base64, struct
bmp_data = base64.b64decode('')

def bmp_info(data):
    r = struct.unpack("<ccIIIIIIHH",data[:30])
    if not (r[0]==b"B" and r[1]==b"M"):
        print("this is not a bmp file")
        return
    else:
        return {"width":r[6],"height":r[7],"color":r[9]}

# 测试
bi = bmp_info(bmp_data)
assert bi['width'] == 28
assert bi['height'] == 10
assert bi['color'] == 16
print('ok')